import{_ as r,c as i,k as e,a as t,H as s,Q as a,C as l,o as n}from"./chunks/framework.76f6b476.js";const L=JSON.parse('{"title":"简介","description":"","frontmatter":{"contributors":[]},"headers":[],"relativePath":"guide.md","filePath":"guide.md","lastUpdated":1704205793000}'),c={name:"guide.md"},d=a('<h1 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">写在前面的话</p><p>开发者和贡献者并没有义务回复您的问题, 您应该具备基本的提问技巧。</p></div><blockquote><p>PhoenixBuilder 是专门为网易版租赁服设计的多功能结构生成工具。<s>兼有服务器管理功能的可选的 Omega 模块</s>(此功能在v5.7.0被移除)<br> 目前建筑方面支持的功能有 欧几里得几何 <s>·ACME文件</s> (已移除) ·BDP (BDump, bdx) <s>·schematic（方块NBT数据丢弃）</s> (此功能在 <a href="./.html">libre分支</a>中存在) 文件的建筑生成，以及图像绘制。</p></blockquote><ul><li>本文中除特殊说明之处外，所述之 FastBuilder 均代表 PhoenixBuilder 。</li></ul><h1 id="源代码开放" tabindex="-1">源代码开放 <a class="header-anchor" href="#源代码开放" aria-label="Permalink to &quot;源代码开放&quot;">​</a></h1><p>PhoenixBuilder 客户端 完整代码已开源（包括验证系统客户端以及核心算法），源代码点击<a href="https://github.com/LNSSPsd/PhoenixBuilder" target="_blank" rel="noreferrer">此处</a>可见（AGPL v3 协议）。 但开放源代码并不意味着免费，您必须拥有 FastBuilder 账号才能使用它进入网易的服务器。</p><p>-以 AGPL v3 协议发布代表着开发组允许您对软件本体进行修改并进行再发布，且并不阻止商业行为。</p><div class="warning custom-block"><p class="custom-block-title">注意</p><p>(p.s. 通过验证并进入服务器是付费项目 所以请不要被 <strong>fb 原版客户端需要打💴所骗</strong>)</p></div><h1 id="基本概念" tabindex="-1">基本概念 <a class="header-anchor" href="#基本概念" aria-label="Permalink to &quot;基本概念&quot;">​</a></h1><p>PhoenixBuilder 与其他程序的不同点在于，存在 <strong>「客户端」</strong> 与 <strong>「服务端」</strong> 概念。玩家正在运行的游戏与 PhoenixBuilder 同为 <strong>客户端</strong> ，而玩家所欲使用 FastBuilder 于之中的租赁服为 <strong>服务端</strong> 。</p><p>众所周知，客户端不一定都要在同一台设备上，所以即使您自己的游戏没有运行也能使用/正常运行FastBuilder。</p><p>由于使用命令行操作，需要用户具备如下基础：</p><ul><li>文件操作能力，能够理解路径和文件层级。</li><li>简单的英语能力，能够认出 “Error” · “Permission denied” · “Not found” 等系统提示之字眼并明白其含义。</li><li>能够区分全角符号与半角符号。</li><li>(最好具有)在命令行界面输入并执行命令的能力。</li></ul><p>请确保您具备以上能力，如果您不具备这样的条件仍执意购买 FastBuilder 导致无法/不会使用，开发组不承担任何责任，更不会提供帮助。</p><h1 id="相关版本介绍" tabindex="-1">相关版本介绍 <a class="header-anchor" href="#相关版本介绍" aria-label="Permalink to &quot;相关版本介绍&quot;">​</a></h1>',15),h={class:"tip custom-block"},u={class:"custom-block-title"},p=e("p",null,[t("版本号格式为 v1.1.4 且有指向于 "),e("a",{href:"https://github.com/LNSSPsd/PhoenixBuilder",target:"_blank",rel:"noreferrer"},"https://github.com/LNSSPsd/PhoenixBuilder"),t(" 的链接")],-1),_={class:"tip custom-block"},b={class:"custom-block-title"},m=e("p",null,[t("版本号格式为 Branche de Liberté 5.7.4-libre 且有指向于 "),e("a",{href:"https://github.com/LNSSPsd/PhoenixBuilder/tree/libre",target:"_blank",rel:"noreferrer"},"https://github.com/LNSSPsd/PhoenixBuilder/tree/libre"),t(" 的链接")],-1),g={class:"tip custom-block"},P={class:"custom-block-title"},B=e("p",null,[t("版本号格式为 Dev 1.1.45141 且有指向于 "),e("a",{href:"https://github.com/rnhws-Team/PhoenixBuilder",target:"_blank",rel:"noreferrer"},"https://github.com/rnhws-Team/PhoenixBuilder"),t(" 的链接")],-1);function f(x,k,v,S,N,q){const o=l("Badge");return n(),i("div",null,[d,e("div",h,[e("p",u,[t("官方分支"),s(o,{type:"fb",text:"官方分支"})]),p]),e("div",_,[e("p",b,[t("libre分支"),s(o,{type:"libre",text:"libre分支"})]),m]),e("div",g,[e("p",P,[t("Dev分支"),s(o,{type:"unsupport",text:"已停止支持"})]),B])])}const A=r(c,[["render",f]]);export{L as __pageData,A as default};
